class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      homeLink: "Home",
      homeMounted: true
    }
  }
  onGreet(age){
    alert(age)
  }
  onChangeLinkName(newName){
    this.setState({
      homeLink: newName
    })
  }

  onChangeHomeMounted() {
    this.setState({
      homeMounted: !this.state.homeMounted
    })
  }
  render (){
    let content = "";
    if(true){
      content = "welcome"
    }
    const user={
      name: "Elsa",
      hobbies: ["Sports","Reading"]
    }
    return (
      <div className="container">
        <div className="row">
          <div className="col-xs-12 col-xs-offset-11">
            <Home homeLink={this.state.homeLink} />
          </div>
        </div>
        <div className="row">
          <div className="col-xs-12 col-xs-offset-11">
            <h1>Hello World!</h1>
            {/* { content } */}
            { true ? "true" : "false" }
          </div>
        </div>
        <div className="row">
          <div className="col-xs-12 col-xs-offset-11">
            {/* <Header name={"Max"} age={12} user={user}/> */}
            <Header 
                name={"Max"} 
                intialAge={12} 
                user={user} 
                greet={this.onGreet} 
                changeLink={this.onChangeLinkName.bind(this)}
                initialName={this.state.homeLink}>
              <p>i am child</p>
            </Header>
          </div>
        </div>
        <hr />
        <button onClick={this.onChangeHomeMounted.bind(this)} className="btn-success">(Un)mount Home Component</button>
      </div>
    )
  }
}

export default App;
